      const response = await fetch('https://opentdb.com/api_token.php?command=request');
      const token = await response.json();
      console.log(token);
      this.setState({
        token,
      });
      localStorage.setItem(token, );
    }

    render() {
      return (
        <div>
          <button
            type="button"
            onClick={ () => this.requestTrivia() }
          >
            Play
          </button>
        </div>
      );
    }
}

